
#include <iostream>
#include <map>
#include <queue>
#include <threads.h>
#include <vector>

using namespace std;

class Solution {
public:
    vector<vector<int>> highestPeak(vector<vector<int>>& isWater) {
        queue<pair<int, int>> queue;
        vector<vector<int>> ground(isWater.size(), vector<int>(isWater[0].size(), isWater.size()* isWater[0].size()));
        for (int y = 0; y < isWater.size(); y++) {
            for (int x = 0; x < isWater[0].size(); x++) {
                if (isWater[y][x]) {
                    queue.emplace(y, x);
                    ground[y][x] = 0;
                }
            }
        }

        int dx[4]{ 1,-1,0,0 };
        int dy[4]{ 0,0,1,-1 };
        while (!queue.empty()) {
            pair<int, int> point = queue.front();
            queue.pop();
            for (int i = 0; i < 4; i++)
            {
                if (point.first + dy[i] < 0 ||
                    point.second + dx[i] < 0 ||
                    point.first + dy[i] > ground.size() - 1 ||
                    point.second + dx[i] > ground[0].size() - 1) {
                    continue;
                }
                if (ground[point.first + dy[i]][point.second + dx[i]] - ground[point.first][point.second] < -1)
                {
                    ground[point.first][point.second] = ground[point.first + dy[i]][point.second + dx[i]] + 1;
                    i = -1;
                }
                else if(ground[point.first + dy[i]][point.second + dx[i]] - ground[point.first][point.second] > 1)
                {
                    ground[point.first + dy[i]][point.second + dx[i]] = ground[point.first][point.second] + 1;
                    queue.emplace(point.first + dy[i], point.second + dx[i]);
                }
            }
        }
        return ground;
    }
};

std::vector<std::vector<int>> process_input(std::string& input)
{
    std::vector<std::vector<int>> res;
    std::vector<int> line;
    for (int i = 1; i < input.size()-1; i++)
    {
	    if (input[i] == '[' || input[i] == ',')
	    {
            continue;
	    }
        if (input[i] == ']')
        {
            res.push_back(line);
            line.clear();
        }
        else
        {
            line.push_back(atoi(&input[i]));
        }
    }
    return res;
}

